var icon = document.getElementById("icon");
var clear = document.getElementsByClassName("clear");
var modulo = document.getElementsByClassName("modulo");
var div = document.getElementsByClassName("division");
var multiplication = document.getElementsByClassName("multiplication");
var subtraction = document.getElementsByClassName("subtraction");
var addition = document.getElementsByClassName("addition");
var equals = document.getElementsByClassName("equals");
var ce = document.getElementsByClassName("deleteCharacter");
var dot = document.getElementsByClassName("dot");

icon.onclick = function(){
    document.body.classList.toggle("dark-theme");
    if(document.body.classList.contains("dark-theme")){
        console.log("dark theme");
        console.log(addition.src);
        icon.src = "./img/sun.png";
        modulo.src = "./img/Vectors/mod_white.svg";
        div.src = "./img/Vectors/div_white.svg";
        multiplication.src = "./img/Vectors/mul_white.svg";
        subtraction.src = "./img/Vectors/sub_white.svg";
        addition.src = "./img/Vectors/add_white.svg";
        equals.src = "./img/Vectors/equals_white.svg";
        ce.src = "./img/Vectors/ce_white.svg";
        dot.src = "./img/Vectors/dot_white.svg";
        console.log(addition.src);
    }else{
        console.log("light theme");
        icon.src = "./img/moon.png";
        modulo.src = "./img/Vectors/mod_dark.svg";
        div.src = "./img/Vectors/div_dark.svg";
        multiplication.src = "./img/Vectors/mul_dark.svg";
        subtraction.src = "./img/Vectors/sub_dark.svg";
        addition.src = "./img/Vectors/add_dark.svg";
        equals.src = "./img/Vectors/equals_dark.svg";
        ce.src = "./img/Vectors/ce_dark.svg";
        dot.src = "./img/Vectors/dot_dark.svg";
    }
}

if (window.matchMedia && window.matchMedia('(prefers-color-scheme: dark)').matches) {
    document.body.classList.toggle("dark-theme");
    if(document.body.classList.contains("dark-theme")){
        icon.src = "./img/sun.png";
        modulo.src = "./img/Vectors/mod_white.svg";
        division.src = "./img/Vectors/div_white.svg";
        multiplication.src = "./img/Vectors/mul_white.svg";
        subtraction.src = "./img/Vectors/sub_white.svg";
        addition.src = "./img/Vectors/add_white.svg";
        equals.src = "./img/Vectors/equals_white.svg";
        ce.src = "./img/Vectors/ce_white.svg";
        dot.src = "./img/Vectors/dot_white.svg";
    }else{
        console.log("light theme");
        icon.src = "./img/moon.png";
        modulo.src = "./img/Vectors/mod_dark.svg";
        div.src = "./img/Vectors/div_dark.svg";
        multiplication.src = "./img/Vectors/mul_dark.svg";
        subtraction.src = "./img/Vectors/sub_dark.svg";
        addition.src = "./img/Vectors/add_dark.svg";
        equals.src = "./img/Vectors/equals_dark.svg";
        ce.src = "./img/Vectors/ce_dark.svg";
        dot.src = "./img/Vectors/dot_dark.svg";
    }
}
